    public class FunnyNameController : ApiController
    {
        [Route("FunnyName")]
        [HttpGet]
        public
       string Get()
        {
            var rng = new Random();
            return RandomName();
        }

        // GET api/values/5
        [Route("FunnyName/GimmeAWholeBunchOfFunnyNames")]
        [HttpGet]
        public IEnumerable<string> GimmeABunchaRandomNames()
        {
            return RandomNames();
        }

        [Route("FunnyName/GimmeXFunnyNames")]
        [HttpGet]
        public IEnumerable<string> GimmeXRandomNames(int cnt)
        {
            return RandomNames(cnt);
        }

        private string[] RandomNames()
        {
            var thisRandom = new Random();
            return RandomNames(thisRandom, Math.Min(thisRandom.Next(1000), 314));
        }

        private string[] RandomNames(int givenCntToProvide)
        {
            return RandomNames(new Random(), givenCntToProvide);
        }

        private string[] RandomNames(Random givenRnd, int givenCntToProvide)
        {
            var result = new List<string>();
            for (int iterator = 0; iterator < Math.Min(1000, givenCntToProvide); iterator++)
            {
                var ThisRandom = RandomName(givenRnd);
                if (result.FindIndex(x => x == ThisRandom) < 0) { result.Add(ThisRandom); }
            }
            return result.ToArray();
        }

        private string RandomName()
        {
            return RandomName(new Random());
        }

        private string RandomName(Random givenRnd)
        {
            if (givenRnd.Next(10) > 7)
            {
                return $"{CamelCaseMe(SomeWords[givenRnd.Next(SomeWords.Length)])} {CamelCaseMe(SomeWords[givenRnd.Next(SomeWords.Length)])}";
            }
            else
            {
                return SomeNames[givenRnd.Next(SomeNames.Length)];
            }
        }

